from odoo import api, fields, modelsclass stockpick(models.Model):    _inherit = 'stock.picking'    extra_location_id = fields.Many2one(comodel_name="extra.location", string="Extra Location", required=False, )class stockqua(models.Model):    _inherit = 'stock.quant'    extra_location_id = fields.Many2one(comodel_name="extra.location",readonly=True, string="Extra Location", required=False,compute="_compute_extra")    extra_location_ids = fields.Many2many(comodel_name="extra.location",readonly=True,string="extra ids",compute='_compute_amount' )    @api.depends()    def _compute_extra(self):        num = 0        for x in self:            num += 1            movelines = x.env['stock.move.line'].search([('product_id', '=', x.product_id.id)])            if movelines:                for extra in movelines.picking_id.extra_location_id:                    if num == 1:                        x.extra_location_id=extra.id                    else:                            if extra == x.extra_location_id:                                x.inventory_quantity+=1                            else:                                quantss=self.search_count([('product_id', '=', x.product_id.id),('lot_id','=',x.lot_id.id),('location_id','=',x.location_id.id),('extra_location_id','=',extra.id)])                                vals={}                                if quantss == 1:                                    vals = {                                        'product_id': x.product_id.id,                                        'location_id': x.location_id.id,                                        'extra_location_id': 8,                                        'lot_id':x.lot_id.id,                                }                                    self.sudo().create(vals)            else:                x.extra_location_id = None    @api.depends()    def _compute_amount(self):        for s in self:            movelines = self.env['stock.move.line'].search([('product_id', '=', s.product_id.id)])            num = 0            s.extra_location_ids = movelines.picking_id.extra_location_id.ids