from odoo import api, fields, modelsclass SaleOrderInherit(models.Model):    _inherit = 'sale.order'    create_analytic= fields.Boolean(string="Create Analytic",  )    def action_confirm(self):        res = super(SaleOrderInherit, self).action_confirm()        if self.create_analytic == True:            values = {                'name': self.name,                'partner_id': self.partner_id.id,            }            create=self.env['account.analytic.account'].create(values)        self.analytic_account_id=create.id        return res